from lib.nextion import nextionfrom ThreadFunctions.DirectLaunch import *import timeimport _threaddef schedule():    display = nextion(12, 13, 9600) # Display-Objekt definieren    response = None    while response is None:        response = display.read()    output = bytearray(response).decode("ASCII")    output = str(output).replace("b'", "")    output = str(output).replace("'", "")    print("Received output data. Output: " + output)    response = None    while response is None:        response = display.read()    hour = bytearray(response).decode("ASCII")    hour = str(hour).replace("b'", "")    hour = str(hour).replace("'", "")    print("Received hour data. Hour: " + hour)    response = None    while response is None:        response = display.read()    minute = bytearray(response).decode("ASCII")    minute = str(minute).replace("b'", "")    minute = str(minute).replace("'", "")    print("Received minute data. Minute: " + minute)    response = None    while response is None:        response = display.read()    second = bytearray(response).decode("ASCII")    second = str(second).replace("b'", "")    second = str(second).replace("'", "")    print("Received second data. Second: " + second)    print("Scheduled launch on output " + output + " in " + hour + ":" + minute + ":" + second + ".")    seconds = int(second)    seconds += int(hour) * 60 * 60    seconds += int(minute) * 60    print("launch in " + str(seconds) + " seconds")    _thread.start_new_thread(schedule_delay, (seconds, output, display))                             def schedule_delay(seconds, output, display):    time.sleep(seconds - 3.6)    print("! ATTENTION: LAUNCH IN 5 SECONDS !")    print(display.cmd("cfgpio 7,2,0"))    time.sleep_ms(200)    print(display.cmd("pio7=1"))    time.sleep(1)    print(display.cmd("pio7=0"))    time.sleep(1)    print(display.cmd("pio7=1"))    time.sleep(1)    print(display.cmd("pio7=0"))    time.sleep_ms(200)    print(display.cmd("cfgpio 7,2,0"))    time.sleep_ms(200)    print(display.cmd("pio7=0"))    print("LAUNCH")    LAUNCH(int(output))